<script>
import Foot from '../../frame/Foot.vue';
import { toRefs } from 'vue';
import Top  from '../../frame/Top.vue'
import {synRequestPost,synRequestGet} from "../../../../static/request"
import { Checkbox } from 'element-ui';
export default {
  name: 'HelloWorld',
  components: {
         Foot,Top
    },
  data () {
    return {
      user: {
          username: "",
          password: "",
      },
      //按钮开关
      switchbutton: false,
      builderList: [],

      getNotOptionalBedBo: {
          token: "",
          roomId: "",
          builderName: "",
          floor: "",
          bedId: 0,
      },

      //已经选的人
      bedmap: {},
      //是否可选床
      status: false,

      object: null,
      //选择按钮
      chooseButoon: false,
    }
  },
   mounted(){

  },
  
  async beforeMount() {
      this.getNotOptionalBedBo.token = getCookie("token");
      this.getNotOptionalBedBo.builderName = getQueryVariable("buildername");
      this.getNotOptionalBedBo.floor = getQueryVariable("floor");
      this.getNotOptionalBedBo.roomId = getQueryVariable("roomId");
      await this.getBedInfo();
  },

  methods: {
    async getBedInfo(){
      let obj = await synRequestPost("/bed/getNotOptionalBed",this.getNotOptionalBedBo);

      if(check(obj)){
        this.status = obj.data.status;
          for(let i = 0 ; i < obj.data.nonOptionalBeds.length ;i++){
              this.$set(this.bedmap,obj.data.nonOptionalBeds[i].bedId,obj.data.nonOptionalBeds[i]);  
          }
          console.log(this.bedmap[2].id);
       


      }
    },


    //选择床位
    async chooseTheBed(id){
      alert(id)
      this.chooseButoon = true;
      this.getNotOptionalBedBo.bedId = id;
      let obj = await synRequestPost("/bed/chooseBed",this.getNotOptionalBedBo);
      if(check(obj)){
        alert("选择成功")
      }else{
        alert("asd");
        this.chooseButoon = false;
      }

      await this.getBedInfo();
    }
  }
}
</script>

